from couchbase.bucket import Bucket

cb = Bucket('couchbase://localhost/default')
cb.upsert('cb_dev_manual', {
    'name': 'Couchbase Developer Guide',
    'language': 'Python',
    'type': 'devguide'
})

print ''

for row in cb.n1ql_query('SELECT name, language WHERE type="devguide"'):
    print '{0} ({1})'.format(row['name'], row['language'])
